import React,{ Component } from 'react';
import logo from './logo.svg';
import './App.css';

class App extends Component{
  constructor(){
    super();

    this.state = {
      monsters : [
        {
          name:'Frankenstine'
        },
        {
          name:'D'
        },
        {
          name:'Frankenstine'
        },
      ]
    };
  }

  render(){
    return (
      <div className="App">
      <header className="App-header">
        <img src={logo} className="App-logo" alt="logo" />
        <p>{this.state.string}</p>
       <button onClick={()=>this.setState({string :"hello mohammad"})}> Change Text </button>
       <br></br>
       <button onClick={()=>this.setState({string :"hello Ali"})}> Change Text </button>
      </header>
    </div>
    );
  }
}

export default App;
